import React, { useEffect, useState } from "react";
import { toast } from "react-toastify";
import { fetchXboxProfile, fetchPSNProfile, fetchSteamProfile } from "../service/profileService";
import { useNavigate } from "react-router-dom";

function HomePage() {
    const [accountInfo, setAccountInfo] = useState(null);
    const [platform, setPlatform] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const loadProfile = async () => {
            const selectedPlatform = localStorage.getItem("platform");

            if (!selectedPlatform) {
                toast.error("No platform selected. Please log in again.");
                navigate("/"); // Redirect to the landing page
                return;
            }

            setPlatform(selectedPlatform);

            try {
                let profileData;

                // Fetch profile based on the platform
                switch (selectedPlatform) {
                    case "xbox":
                        profileData = await fetchXboxProfile();
                        break;
                    case "psn":
                        profileData = await fetchPSNProfile();
                        break;
                    case "steam":
                        profileData = await fetchSteamProfile();
                        break;
                    default:
                        toast.error("Unsupported platform. Please log in again.");
                        navigate("/");
                        return;
                }

                setAccountInfo(profileData);
                localStorage.setItem("profileData", JSON.stringify(profileData)); // Save profile data locally
            } catch (error) {
                console.error(error);
                toast.error("Failed to fetch profile information.");
                navigate("/");
            }
        };

        loadProfile();
    }, [navigate]);

    return (
        <div className="gamerhub">
            <header className="header">
                <h1 className="logo">GamerHUB</h1>
                <nav className="navbar">
                    <button className="nav-button">SEARCH</button>
                    <button className="nav-button">NEWS</button>
                    <div className="account-section">
                        {accountInfo && (
                            <img
                                src={accountInfo.appDisplayPicRaw || accountInfo.avatar}
                                alt="Profile"
                                style={{ borderRadius: "50%", width: "50px", height: "50px" }}
                            />
                        )}
                    </div>
                </nav>
            </header>
            <main className="main-content">
                <section className="platform-info">
                    <h2>{platform ? `${platform.toUpperCase()} Profile` : "Profile"}</h2>
                    {accountInfo ? (
                        <div>
                            <p>GAMERTAG: {accountInfo.gamertag || accountInfo.username}</p>
                            <p>DISPLAY NAME: {accountInfo.gameDisplayName || accountInfo.displayName}</p>
                        </div>
                    ) : (
                        <p>Loading profile information...</p>
                    )}
                </section>
            </main>
        </div>
    );
}

export default HomePage;
