// import React, { useState, useEffect } from 'react';

// import { PublicClientApplication } from '@azure/msal-browser';
{/*
    This page will be used to retrieve msla token from the user when they login their miscrosoft account
    using azuro microsoft, playfab and react
    currently the backend is making an api call that requests the token as a header.. however the token needs to be exchanged
    from msla.. this page will be used to retrieve the token and send it to the backendSigns the user in using a Xbox Live Token from an external server backend, returning a session identifier that can subsequently be used for API calls which require an authenticated user

HTTP

Copy
POST https://titleId.playfabapi.com/Server/LoginWithXbox
Request Header
Name	Required	Type	Description
X-SecretKey	True	
string

This API requires a title secret key, available to title admins, from PlayFab Game Manager.

Request Body
Name	Required	Type	Description
XboxToken	True	
string

Token provided by the Xbox Live SDK/XDK method GetTokenAndSignatureAsync("POST", "https://playfabapi.com/", "").
URL to use for the MSAL 
https://login.microsoftonline.com/common/	

*/
}
import React, { useState, useEffect } from 'react'; // Ensure useEffect is imported
import { PublicClientApplication } from '@azure/msal-browser';

// MSAL Configuration
const msalConfig = {
  auth: {
    clientId: "0a23b968-9e79-4cba-b337-862adab7a8e2", // Replace with your Azure AD app's client ID
    authority: "https://login.microsoftonline.com/common/", // Common authority for all Microsoft accounts
    redirectUri: "http://localhost:3000", // Replace with your application's redirect URI
  },
};

const LoginWithMicrosoft = () => {
  const [msalInstance, setMsalInstance] = useState(null);
  const [token, setToken] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Initialize MSAL instance
    const instance = new PublicClientApplication(msalConfig);
    setMsalInstance(instance);
  }, []); // useEffect runs once to initialize the instance

  const login = async () => {
    if (!msalInstance) {
      setError("MSAL instance not initialized yet.");
      return;
    }

    try {
      // Ensure the MSAL instance is initialized
      if (!msalInstance.initialize) {
        throw new Error("MSAL instance not initialized.");
      }

      // Trigger MSAL login
      const loginResponse = await msalInstance.loginPopup({
        scopes: ["User.ReadWrite"],
    });

      console.log("Login response:", loginResponse);

      // Acquire token silently for the logged-in user
      const tokenResponse = await msalInstance.acquireTokenSilent({
        scopes: ["XboxLive.signin"],
        account: loginResponse.account,
      });

      console.log("Token response:", tokenResponse);
      setToken(tokenResponse.accessToken);
    } catch (err) {
      setError(err.message);
      console.error("Error during login:", err);
    }
  };

  return (
    <div>
      <h1>Test Xbox Token Retrieval</h1>
      <button onClick={login}>Login and Get Token</button>
      {token && <p>Token retrieved: {token}</p>}
      {error && <p style={{ color: "red" }}>Error: {error}</p>}
    </div>
  );
};

export default LoginWithMicrosoft;
