import React, { useEffect } from "react";
import { toast, ToastContainer } from "react-toastify";

const apiUrl = 'http://localhost:8080';

export const getRecentGames = async () => {
    const uhs = localStorage.getItem("uhs");
    const XSTS_token = localStorage.getItem("XSTS_token");

    if (!uhs || !XSTS_token) {
        toast.error("Xbox authentication tokens missing. Please log in again.");
        throw new Error("Xbox authentication tokens missing.");
    }

    try {
        const response = await fetch(`${apiUrl}/api/xbox/recent-games`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                Authorization: `XBL3.0 x=${uhs};${XSTS_token}`,
            },
        });

        if (!response.ok) {
            throw new Error(`Failed to fetch recent games. Status: ${response.status}`);
        }

        return await response.json();
    } catch (error) {
        console.error(error);
        toast.error("Failed to fetch recent games.");
        throw error;
    }
};
