    This function switches the user
    account by updating user current platform
    */
}

export function switchUserAccount(targetPlatform, navigate) {
  if (!navigate) {
    console.error("navigate function is required for switching account");
    return;
  }
  // Set the new platform and navigate to the desired page (e.g. home)
  localStorage.setItem("platform", targetPlatform);
  navigate("/"); // Adjust the route as needed
}
