import React, { useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { exchangeOAuthTokens } from "../service/authService";

function OAuthCallback() {
    const { platform } = useParams(); // Extract platform from URL
    const navigate = useNavigate();

    useEffect(() => {
        const exchangeCodeForTokens = async () => {
            const urlParams = new URLSearchParams(window.location.search);
            const code = urlParams.get("code");

            if (code && platform) {
                try {
                    await exchangeOAuthTokens(platform, code); // Call service to exchange tokens
                    navigate("/main"); // Redirect to the main page
                } catch (error) {
                    console.error(error);
                }
            }
        };

        exchangeCodeForTokens();
    }, [platform, navigate]);

    return (
        <div className="oauth-login" style={{ textAlign: "center", marginTop: "50px" }}>
            <h1>Logging in with {platform ? platform.toUpperCase() : "Platform"}...</h1>
        </div>
    );
}

export default OAuthCallback;
