import React, { useEffect } from "react";
import { toast } from "react-toastify";

const apiUrl = 'http://localhost:8080';

function XboxLogin() {
    useEffect(() => {
        const exchangeCodeForTokens = async () => {
            const urlParams = new URLSearchParams(window.location.search);
            const code = urlParams.get("code");

            if (code) {
                try {
                    const response = await fetch(`${apiUrl}/api/auth/callback/xbox?code=${code}`, {
                        method: "GET",
                        credentials: "include",
                    });

                    if (!response.ok) {
                        throw new Error(`Failed to retrieve tokens. Status: ${response.status}`);
                    }

                    const data = await response.json();
                    localStorage.setItem("uhs", data.uhs);
                    localStorage.setItem("XSTS_token", data.XSTS_token);

                    toast.success("Xbox login successful!");
                    window.location.href = "/main"; // Redirect to the main page
                } catch (error) {
                    toast.error(`Error during Xbox login: ${error.message}`);
                }
            }
        };

        exchangeCodeForTokens();
    }, []);

    return (
        <div className="xbox-login" style={{ textAlign: "center", marginTop: "50px" }}>
            <h1>Logging in with Xbox...</h1>
        </div>
    );
}

export default XboxLogin;
