import React, { useEffect } from "react";
import { View, Text, Button, StyleSheet } from "react-native";
import { useNavigation } from "@react-navigation/native";
import { Linking } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import Toast from "react-native-toast-message";
import { handleLogin, exchangeCodeForTokens } from "../service/XboxAuthService";

const LandingPage = () => {
  const navigation = useNavigation();

  useEffect(() => {
    const handleXboxLogin = async () => {
      await exchangeCodeForTokens();
      // Set platform in AsyncStorage
      await AsyncStorage.setItem("platform", "xbox");
      // Navigate to the main screen (adjust the route name as needed)
      navigation.reset({ routes: [{ name: "HomePage" }] });
    };

    const checkInitialURL = async () => {
      const url = await Linking.getInitialURL();
      if (url) {
        // Parse the URL to check for the code parameter
        const parsedUrl = new URL(url);
        const code = parsedUrl.searchParams.get("code");
        if (code) {
          handleXboxLogin();
        }
      }
    };

    checkInitialURL();
  }, [navigation]);

  const handlePlatformLogin = async (platform) => {
    if (platform === "xbox") {
      await handleLogin();
    }
    // Add similar logic for PSN and Steam if needed
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Welcome to GamerHub</Text>
      <Text style={styles.subtitle}>Select a platform to log in:</Text>
      <View style={styles.buttonContainer}>
        <Button
          title="Xbox Login"
          onPress={() => handlePlatformLogin("xbox")}
          color="green"
        />
        <Button
          title="PSN Login"
          onPress={() => handlePlatformLogin("psn")}
          color="blue"
        />
        <Button
          title="Steam Login"
          onPress={() => handlePlatformLogin("steam")}
          color="gray"
        />
      </View>
      <Toast />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
  },
  title: {
    fontSize: 28,
    fontWeight: "bold",
    marginBottom: 20,
  },
  subtitle: {
    fontSize: 18,
    marginBottom: 20,
  },
  buttonContainer: {
    width: "100%",
    flexDirection: "row",
    justifyContent: "space-around",
  },
});

export default LandingPage;
