import React, { useState, useEffect } from 'react'; // Ensure useEffect is imported
import { PublicClientApplication } from '@azure/msal-browser';

// MSAL Configuration
const msalConfig = {
  auth: {
    clientId: "0a23b968-9e79-4cba-b337-862adab7a8e2", // Replace with your Azure AD app's client ID
    authority: "https://login.microsoftonline.com/consumers/", // Common authority for all Microsoft accounts
    redirectUri: "http://localhost:3000", // Replace with your application's redirect URI
  },
};

const LoginWithMicrosoft = () => {
  const [msalInstance, setMsalInstance] = useState(null);
  const [token, setToken] = useState(null);
  const [error, setError] = useState(null);
  const [xboxToken, setXboxToken] = useState(null); // Added xboxToken state

  useEffect(() => {
    const initializeMsal = async () => {
      const instance = new PublicClientApplication(msalConfig);
      await instance.initialize(); // Added initialization
      setMsalInstance(instance);
    };

    initializeMsal();
  }, []); // useEffect runs once to initialize the instance

  const login = async () => {
    if (!msalInstance) {
      setError("MSAL instance not initialized yet.");
      return;
    }

    try {
      await msalInstance.initialize(); // Ensure initialization before login

      const loginResponse = await msalInstance.loginPopup({
        scopes: ["openid", "profile", "offline_access", "XboxLive.signin"],
      });

      const tokenResponse = await msalInstance.acquireTokenSilent({
        scopes: ["openid", "profile", "offline_access", "XboxLive.signin"],
        account: loginResponse.account,
      });


      console.log("Token response:", tokenResponse);
      setToken(tokenResponse.accessToken);
    } catch (err) {
      setError(err.message);
      console.error("Error during login:", err);
    }
  };

  const returnTokenToServer = async () => {
    if (!token) {
        setError("No token to return to server.");
        return;
    }

    try {
        const { token: xstsToken, userHash } = await getXSTSToken(token);
        const formattedToken = `XBL3.0 x=${userHash};${xstsToken}`;
        console.log("Formatted token:", formattedToken);

        const response = await fetch("http://localhost:8080/api/auth/xbox", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ xboxToken: formattedToken, createAccount: false }),
        });

        if (!response.ok) {
            const errorData = await response.text();
            throw new Error(`Xbox server error: ${errorData}`);
        }

        const data = await response.json();
        console.log("Server response:", data);
    } catch (err) {
        setError(err.message);
        console.error("Error during login and token submission:", err);
    }
};


  // login xbox server - 
  const loginXboxServer = async (xboxToken) =>{
    try{
        const response = await fetch("http://localhost:8080/api/auth/xbox", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ xboxToken, createAccount: false }),
        });
        if (!response.ok) {
            throw new Error(`Server responded with ${response.status}`);
        }
        const data = await response.json();
        console.log("Server response:", data);
    }catch(e){
        setError(e.message);
        console.error("Error logging into xbox server:", e);
    }
  }

  const loginAndSendToken = async () => {
    if (!xboxToken) {
      setError("No Xbox Live token available. Please log in first.");
      return;
    }
    try {
      // Get XSTS token with user hash
      const xstsResponse = await getXSTSToken(xboxToken);
      const userHash = xstsResponse.userHash; // Extract userHash correctly

      // Format token for PlayFab
      const formattedToken = `XBL3.0 x=${userHash};${xstsResponse.Token}`;
      console.log("Formatted token:", formattedToken);

      // Send to backend
      const xboxResponse = await fetch("http://localhost:8080/api/auth/xbox", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ xboxToken: formattedToken, createAccount: false }),
      });

      if (!xboxResponse.ok) {
        const errorData = await xboxResponse.text();
        throw new Error(`Xbox server error: ${errorData}`);
      }

      const xboxData = await xboxResponse.json();
      console.log("Xbox server response:", xboxData);
    } catch (err) {
      setError(err.message);
      console.error("Error during login and token submission:", err);
    }
  };


  return (
    <div>
      <h1>Test Xbox Token Retrieval</h1>
      <button onClick={login}>Login and Get Token</button>
      <button onClick={returnTokenToServer}>Send Token to Server</button>
      {token && <p>Token retrieved: {token}</p>}
      {error && <p style={{ color: "red" }}>Error: {error}</p>}
    </div>
  );
};

export default LoginWithMicrosoft;
