import React from "react";

const LoginWithMicrosoft = () => {
  const handleLogin = () => {
    window.location.href = "http://localhost:8080/auth/xbox/login";
  };

  return (
    <div>
      <h1>Welcome to GamerHub</h1>
      <button onClick={handleLogin}>Login with Xbox</button>
    </div>
  );
};

export default LoginWithMicrosoft;
