import React, { useState } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const apiURL = "http://localhost:8080";

function togglePassword() {
  let password = document.getElementById("passwordInput");
  if (password.type === "password") {
    password.type = "text";
  } else {
    password.type = "password";
  }
}

function AuthenticationPage() {
  const [isAuthenticated, setAuthentication] = useState(false); // sets authentication if valid
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [xboxToken, setXboxToken] = useState(""); // For Xbox-specific login

  const xboxLogin = async (e) => {
    e.preventDefault();

    if (!xboxToken) {
      toast.error("Xbox token is required for login.");
      return;
    }

    try {
      const response = await fetch(`${apiURL}/api/auth/xbox`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ XboxToken: xboxToken, CreateAccount: false }), // Xbox-specific fields
      });

      if (!response.ok) {
        const errorMessage = await response.text();
        throw new Error(errorMessage || "Failed to login with Xbox.");
      }

      const data = await response.json();
      setAuthentication(true);
      toast.success(`Welcome, ${data.gamertag || "Xbox User"}!`);
    } catch (error) {
      console.error("Error during Xbox login:", error);
      toast.error(error.message || "Failed to login with Xbox.");
    }
  };

  return (
    <div className="authentication-page">
      <h1>Login</h1>
      <form onSubmit={xboxLogin}>
        <div>
          <label htmlFor="xboxToken">Xbox Token</label>
          <input
            type="text"
            id="xboxToken"
            placeholder="Enter your Xbox token"
            value={xboxToken}
            onChange={(e) => setXboxToken(e.target.value)}
          />
        </div>
        <button type="submit">Login with Xbox</button>
      </form>
      <hr />
      <div>
        <h2>Don't have an account?</h2>
        <a href="https://www.xbox.com/en-US/live">Create an Xbox Account</a>
      </div>
    </div>
  );
}

export default AuthenticationPage;
