// SwitchUserAccount.js
export const switchUserAccount = (platform, navigate) => {
    const handleRedirect = (url) => {
      // Clear existing platform flag during auth flow
      localStorage.removeItem("platform");
      window.location.href = url;
    };

    if (platform === "xbox") {
      const xstsToken = localStorage.getItem("XSTS_token");
      const uhs = localStorage.getItem("uhs");
      if (!xstsToken || !uhs) {
        console.log("Initiating Xbox authentication...");
        handleRedirect("http://localhost:8080/api/auth/login/xbox");
        return;
      }
    } else if (platform === "steam") {
      const steamId = localStorage.getItem("steamId");
      if (!steamId) {
        console.log("Initiating Steam authentication...");
        handleRedirect("http://localhost:8080/api/auth/login/steam");
        return;
      }
    }

    // Set platform and preserve current path
    localStorage.setItem("platform", platform);
    navigate("/main", { replace: true, state: { fromSwitch: true } });
};
