import React, { useEffect, useState, useRef } from "react";
import { fetchAllNews, searchForGame, getGenres } from "../service/NewsService";
import { fetchGameTrailers } from "../service/GameTrailersFromYoutubeService";
import { useSearchParams, useNavigate } from "react-router-dom";
import "../assests/News.css";
import { checkAccountType } from "../utility/CheckAccountType";

function News() {
  const [searchParams] = useSearchParams();
  const selectedGenre = searchParams.get("genre") || "";
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const textRef = useRef("");
  const navigate = useNavigate();
  const username = localStorage.getItem("username");
  const [genres, setGenres] = useState([]);
  const [hoveredGameSlug, setHoveredGameSlug] = useState(null);

  // This stores trailers keyed by slug: { [slug]: trailerData }
  const [trailers, setTrailers] = useState({});

  // -- Fetch news data and genres as before --
  useEffect(() => {
    async function getNews() {
      try {
        setLoading(true);
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    getNews();
  }, [selectedGenre]);

  useEffect(() => {
    async function fetchGenreOptions() {
      try {
        const data = await getGenres();
        setGenres(data);
      } catch (err) {
        console.error("Error fetching genres:", err);
      }
    }
    fetchGenreOptions();
  }, []);

  // -- Searching logic as before --
  const handleSearch = async () => {
    const inputValue = textRef.current.value.trim();
    try {
      if (!inputValue) {
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } else {
        const results = await searchForGame(inputValue);
        setNewsData(results || []);
      }
    } catch (err) {
      setError(err);
    }
    textRef.current.value = "";
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") handleSearch();
  };

  const handleCardClick = (game) => {
    navigate(`/news/game/${game.id}`);
  };

  /**
   * On hover, check if we already have a trailer for this game.
   * If not, fetch it from the API.
   */
  const handleMouseEnter = async (item) => {
    setHoveredGameSlug(item.slug);
    // If we already have a trailer or we tried fetching before (null),
    // don't fetch again
    if (trailers[item.slug] !== undefined) return;

    try {
      const data = await fetchGameTrailers({ gameName: item.name });
      const trailer = data.items?.[0] || null;
      setTrailers((prev) => ({ ...prev, [item.slug]: trailer }));
    } catch (err) {
      console.error("Error fetching trailer for", item.name, err);
      setTrailers((prev) => ({ ...prev, [item.slug]: null }));
    }
  };

  const handleMouseLeave = () => {
    setHoveredGameSlug(null);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div className="news-page">
      {/* Sidebar or Top Area for Filter/Search */}
      <div className="filter-area">
        <h1>News and Trending Games For {username}</h1>

        <div className="filter-bar">
          <div className="genre-filter">
            <label htmlFor="genre-select">Filter by Genre:</label>
            <select
              id="genre-select"
              value={selectedGenre}
              onChange={(e) =>
                navigate(e.target.value ? `/news?genre=${e.target.value}` : "/news")
              }
            >
              <option value="">All Genres</option>
              {genres.map((genre) => (
                <option key={genre.slug} value={genre.slug}>
                  {genre.name}
                </option>
              ))}
            </select>
          </div>

          <div className="search-bar">
            <input
              ref={textRef}
              type="text"
              placeholder="Search for Game"
              onKeyDown={handleKeyPress}
            />
            <button onClick={handleSearch}>Search</button>
          </div>
        </div>
      </div>

      {/* Main content area (cards) */}
      <div className="news-content">
        {newsData.map((item) => {
          const isHovered = hoveredGameSlug === item.slug;
          const trailerData = trailers[item.slug];
          const hasTrailer = trailerData !== undefined && trailerData !== null;

          return (
            <div
              className="game-card"
              key={item.slug}
              onMouseEnter={() => handleMouseEnter(item)}
              onMouseLeave={handleMouseLeave}
              onClick={() => handleCardClick(item)}
            >
              <div className="card-media">
                {isHovered && hasTrailer ? (
                  <iframe
                    src={`https://www.youtube.com/embed/${trailerData.id.videoId}?autoplay=1&mute=1&start=15`}
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                    title={item.name}
                  ></iframe>
                ) : (
                  <img
                    src={item.background_image}
                    alt={item.name}
                    className="card-image"
                  />
                )}
                {isHovered && (
                  <button
                    className="play-full-video-button"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleCardClick(item);
                    }}
                  >
                    Play full video
                  </button>
                )}
              </div>
              <div className="card-body">
                <h2>{item.name}</h2>
                {/* other info */}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default News;
