import React, { useEffect, useState } from "react";

function App() {
  const [gamertagInfo, setGamertagInfo] = useState(null);
  const [uhs, setUhs] = useState(null);
  const [XSTS_token, setXstsToken] = useState(null);

  useEffect(() => {
    // Extract the authorization code from the URL after login redirect
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get("code");

    if (code) {
      // Call backend to handle token exchange and retrieve uhs and XSTS_token
      fetch(`http://localhost:8080/api/auth/callback?code=${code}`, {
        method: "GET",
        credentials: "include",
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          console.log("Token Exchange Data:", data);
          if (data.uhs && data.XSTS_token) {
            setUhs(data.uhs);
            setXstsToken(data.XSTS_token);
          } else {
            console.error("Missing uhs or XSTS_token in response");
          }
        })
        .catch((error) => {
          console.error("Error during token exchange:", error);
        });
    }
  }, []);

  const fetchProfile = () => {
    if (!uhs || !XSTS_token) {
      console.error("Missing uhs or XSTS_token. Cannot fetch profile.");
      return;
    }

    // Fetch profile data using uhs and XSTS_token
    fetch("http://localhost:8080/api/xbox/profile", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `XBL3.0 x=${uhs};${XSTS_token}`,
      },
      credentials: "include",
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error fetching profile! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((profileData) => {
        console.log("Profile Data:", profileData);
        setGamertagInfo(profileData);
      })
      .catch((error) => {
        console.error("Error fetching profile:", error);
      });
  };

  const handleLogin = () => {
    // Redirect to login endpoint to initiate the OAuth process
    window.location.href = "http://localhost:8080/api/auth/login";
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Welcome to GamerHub</h1>
      {gamertagInfo ? (
        <div>
          <h2>Gamertag: {gamertagInfo.gamertag}</h2>
          <p>Display Name: {gamertagInfo.gameDisplayName}</p>
          <img
            src={gamertagInfo.appDisplayPicRaw}
            alt="Profile Pic"
            style={{ borderRadius: "50%", width: "100px", height: "100px" }}
          />
        </div>
      ) : (
        <div>
          <button
            onClick={handleLogin}
            style={{
              padding: "10px 20px",
              fontSize: "16px",
              cursor: "pointer",
              backgroundColor: "#0078D7",
              color: "#ffffff",
              border: "none",
              borderRadius: "5px",
            }}
          >
            Login with Xbox
          </button>
          <button
            onClick={fetchProfile}
            style={{
              padding: "10px 20px",
              fontSize: "16px",
              cursor: "pointer",
              backgroundColor: "#28a745",
              color: "#ffffff",
              border: "none",
              borderRadius: "5px",
              marginLeft: "10px",
            }}
          >
            Fetch Profile
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
