import React, { useEffect, useState } from "react";

function App() {
  const [gamertagInfo, setGamertagInfo] = useState(null);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get("code");

    if (code) {
      fetch("http://localhost:8080/api/auth/callback?code=" + code, {
        credentials: 'include'
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          return response.json();
        })
    }
  }, []);

  const handleLogin = () => {
    window.location.href = "http://localhost:8080/api/auth/login";
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Welcome to GamerHub</h1>
      {gamertagInfo ? (
        <div>
          <h2>Gamertag: {gamertagInfo.username}</h2>
          <p>Recent Games: {gamertagInfo.recentGames.join(", ")}</p>
          <p>Friends: {gamertagInfo.friends.join(", ")}</p>
        </div>
      ) : (
        <button
          onClick={handleLogin}
          style={{
            padding: "10px 20px",
            fontSize: "16px",
            cursor: "pointer",
            backgroundColor: "#0078D7",
            color: "#ffffff",
            border: "none",
            borderRadius: "5px",
          }}
        >
          Login with Xbox
        </button>
      )}
    </div>
  );
}

export default App;
