import React from "react";
import { toast } from "react-toastify";
import { useNavigate } from 'react-router-dom';
const apiUrl = 'http://localhost:8080';

function LandingPage() {
    const nav = useNavigate();
    const handleLogin = async (platform) => {
        try {
            // Initiate the login process for the selected platform
            const response = await fetch(`${apiUrl}/api/auth/login/${platform}`, {
                method: "GET",
                credentials: "include",
            });

            if (!response.ok) {
                throw new Error(`Failed to initiate login for ${platform}. Status: ${response.status}`);
            }

            const data = await response.json();
            if (data.redirectUrl) {
                // Save platform selection
                localStorage.setItem("platform", platform);

                // Redirect to the platform's login page
                window.location.href = data.redirectUrl;
                nav('/HomePage');

            } else {
                toast.error("Redirect URL not found.");
            }
        } catch (error) {
            toast.error(`Login failed: ${error.message}`);
        }
    };

    return (
        <div className="landing-page" style={{ textAlign: "center", marginTop: "50px" }}>
            <h1>Welcome to GamerHub</h1>
            <p>Select a platform to log in:</p>
            <div style={{ display: "flex", justifyContent: "center", gap: "20px", marginTop: "20px" }}>
                <button onClick={() => handleLogin("xbox")} style={buttonStyle("green")}>
                    Xbox Login
                </button>
                <button onClick={() => handleLogin("psn")} style={buttonStyle("blue")}>
                    PSN Login
                </button>
                <button onClick={() => handleLogin("steam")} style={buttonStyle("gray")}>
                    Steam Login
                </button>
            </div>
        </div>
    );
}

const buttonStyle = (color) => ({
    padding: "10px 20px",
    fontSize: "16px",
    cursor: "pointer",
    backgroundColor: color,
    color: "#ffffff",
    border: "none",
    borderRadius: "5px",
});

export default LandingPage;
