// SwitchUserAccount.js
export const switchUserAccount = (platform, navigate) => {
    if (platform === "xbox") {
      const xstsToken = localStorage.getItem("XSTS_token");
      const uhs = localStorage.getItem("uhs");
      if (!xstsToken || !uhs) {
        // If missing Xbox tokens, redirect to your Xbox login endpoint
        console.log("No Xbox tokens found. Redirecting to Xbox login...");
        // e.g. handleLogin() or window.location.href = "http://localhost:8080/api/auth/login"
        return;
      }
    } else if (platform === "steam") {
      const steamId = localStorage.getItem("steamId");
      if (!steamId) {
        // If missing Steam tokens, redirect to your Steam login
        console.log("No Steam tokens found. Redirecting to Steam login...");
        // e.g. handleSteamLogin()
        return;
      }
    }

    // If tokens exist, just set the platform and go
    localStorage.setItem("platform", platform);
    navigate("/main", { replace: true });
  };
