{
    /*
    This function switches the user
    account by updating user current platform
    */
}
export const switchUserAccount = (platform, navigate) => {
	// Clear token if account tokens are not stored separately
	localStorage.removeItem('jwtToken');
	localStorage.setItem("platform", platform);
	// Reload to reinitialize the HomePage with the new platform:
	window.location.reload(); // Or use navigate('/') if you want a redirect
};
