import React, { Component } from "react";
import { toast } from "react-toastify";
import "../assests/HomePage.css";
import { fetchXboxProfile, fetchPSNProfile, fetchSteamProfile } from "../service/profileService";
import { fetchXboxFriends } from "../service/friendsService";

class HomePage extends Component {
    state = {
        accountInfo: null,
        isFetching: true,
        friends: [],
        isFetchingFriends: true,
    };

    async componentDidMount() {
        const platform = localStorage.getItem("platform");
        if (!platform) {
            toast.error("No platform selected. Please log in again.");
            window.location.href = "/";
            return;
        }

        const profileFetchers = {
            xbox: fetchXboxProfile,
            psn: fetchPSNProfile,
            steam: fetchSteamProfile,
        };

        const fetchProfile = profileFetchers[platform];
        if (!fetchProfile) {
            toast.error("Unsupported platform. Please log in again.");
            window.location.href = "/";
            return;
        }

        try {
            const accountInfo = await fetchProfile();
            this.setState({ accountInfo, isFetching: false });

            if (platform === "xbox") {
                try {
                    const friends = await fetchXboxFriends();
                    this.setState({ friends, isFetchingFriends: false });
                } catch {
                    toast.error("Failed to fetch friends list.");
                    this.setState({ isFetchingFriends: false });
                }
            }
        } catch {
            toast.error("Failed to fetch profile information.");
            localStorage.removeItem("platform");
            window.location.href = "/";
        }
    }

    render() {
        const { accountInfo, isFetching, friends, isFetchingFriends } = this.state;

        if (isFetching) return <p>Loading profile...</p>;

        return (
            <div className="gamerhub">
                <header className="header">
                    <h1 className="logo">GamerHUB</h1>
                    <nav className="navbar">
                        <button className="nav-button">NEWS</button>
                        <button className="nav-button">SEARCH</button>
                        <div className="account-section">
                            {accountInfo && (
                                <>
                                    <p>{accountInfo.gamertag}</p>
                                    <img
                                        src={accountInfo.appDisplayPicRaw || "/default-avatar.png"}
                                        alt="Profile"
                                        style={{ borderRadius: "50%", width: "50px", height: "50px" }}
                                    />
                                </>
                            )}
                        </div>
                    </nav>
                </header>
                <main className="main-content">
                    <section className="recent-games">
                        <h2>Recent Games</h2>
                        <div className="content-box">Display recent games played here...</div>
                    </section>
                    <section className="news-feed">
                        <h2>News Feeds</h2>
                        <div className="content-box">Display top 5 recent game news here...</div>
                    </section>
                    <FriendsList friends={friends} isFetchingFriends={isFetchingFriends} />
                </main>
                <footer className="footer">
                    <p>About: Created by Ahmad Bishara</p>
                    <a href="https://github.com/ahmadb123" target="_blank" rel="noopener noreferrer">GitHub: ahmadb123</a>
                </footer>
            </div>
        );
    }
}

const FriendsList = ({ friends, isFetchingFriends }) => (
    <section className="friends-list">
        <h2>Friends</h2>
        <div className="content-box">
            {isFetchingFriends ? (
                <p>Loading friends...</p>
            ) : friends.length > 0 ? (
                <ul>
                    {friends.map((friend, index) => (
                        <li key={index}>
                            <p>{friend.gamertag}</p>
                            <p>Favorites: {friend.isFavorite ? "Yes" : "No"}</p>
                            <img
                                src={friend.displayPicRaw || "/default-avatar.png"}
                                alt={`Avatar of ${friend.gamertag}`}
                                onError={(e) => (e.target.src = "/default-avatar.png")}
                                style={{ borderRadius: "50%", width: "50px", height: "50px" }}
                            />
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No friends found.</p>
            )}
        </div>
    </section>
);

export default HomePage;
