import React, { useEffect, useState, useRef } from "react";
import { fetchAllNews, searchForGame, getGenres } from "../service/NewsService";
import { useSearchParams, useNavigate } from "react-router-dom";
import "../assests/News.css";
import { checkAccountType } from "../utility/CheckAccountType";

function News() {
  const [searchParams] = useSearchParams();
  const selectedGenre = searchParams.get("genre") || "";
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const textRef = useRef("");
  const navigate = useNavigate();
  const username = localStorage.getItem("username");
  const [genres, setGenres] = useState([]);
  const [hoveredGameSlug, setHoveredGameSlug] = useState(null);

  const handleKeyPress = (event) => {
    if (event.key === "Enter") handleSearch();
  };

  const handleImageClick = (game) => {
    navigate(`/news/game/${game.id}`);
  };

  const handleSearch = async () => {
    const inputValue = textRef.current.value.trim();
    try {
      if (!inputValue) {
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } else {
        const results = await searchForGame(inputValue);
        setNewsData(results || []);
      }
    } catch (err) {
      setError(err);
    }
    textRef.current.value = "";
  };

  useEffect(() => {
    async function getNews() {
      try {
        setLoading(true);
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    getNews();
  }, [selectedGenre]);

  useEffect(() => {
    async function fetchGenreOptions() {
      try {
        const data = await getGenres();
        setGenres(data);
      } catch (err) {
        console.error("Error fetching genres:", err);
      }
    }
    fetchGenreOptions();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <>
      <h1>News and Trending Games For {username}</h1>

      {/* Genre Filter */}
      <div style={{ marginBottom: "1rem" }}>
        <label htmlFor="genre-select" style={{ marginRight: "0.5rem" }}>
          Filter by Genre:
        </label>
        <select
          id="genre-select"
          value={selectedGenre}
          onChange={(e) =>
            navigate(e.target.value ? `/news?genre=${e.target.value}` : "/news")
          }
        >
          <option value="">All Genres</option>
          {genres.map((genre) => (
            <option key={genre.slug} value={genre.slug}>
              {genre.name}
            </option>
          ))}
        </select>
      </div>

      {/* Search Bar */}
      <div className="search-bar">
        <input
          ref={textRef}
          type="text"
          placeholder="Search for Game"
          onKeyDown={handleKeyPress}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* News Grid */}
      <div className="news-grid">
        {newsData.map((item) => (
          <div
            className="game-card-medium discover__content__game-card-medium"
            key={item.slug}
          >
            <div
              className="game-card-medium__wrapper"
              role="button"
              tabIndex={0}
              onClick={() => handleImageClick({ id: item.id })}
              onMouseEnter={() => setHoveredGameSlug(item.slug)}
              onMouseLeave={() => setHoveredGameSlug(null)}
            >
              {/* Media Section */}
              <div className="game-card-medium__media">
                <div className="game-card-medium__media-wrapper">
                  <div className="game-card-video">
                    {/* “Play full video” button (static example) */}
                    <div
                      className="watch-full-video-button game-card-video__watch-full-button"
                      role="button"
                      tabIndex={0}
                    >
                      <span className="SVGInline">
                        <svg
                          className="SVGInline-svg"
                          width="23"
                          height="18"
                          viewBox="0 0 23 18"
                          xmlns="http://www.w3.org/2000/svg"
                        >
                          <g fill="none">
                            <path
                              d="M3.986 17.166c.777.065 2.302.122 4.27.168 1.3.031 2.953.059 3.206.059.09 0 .244 0 .461-.003.36-.002.758-.008 1.188-.016a97.225 97.225 0 003.578-.127 49.49 49.49 0 002.593-.206l.071.001c.795-.057 1.364-.273 1.944-.944.46-.493.835-1.842.839-2.317l.035-.333a41.2 41.2 0 00.077-.89c.071-.92.114-1.84.114-2.695V7.914a35.83 35.83 0 00-.114-2.553c-.039-.507-.116-1.31-.116-1.321 0-.385-.367-1.725-.804-2.217-.594-.658-1.171-.877-1.893-.935l-.198-.002C17.888.784 16.15.71 14.22.663c-1.177-.03-2.466-.049-2.76-.049-.32 0-1.568.022-2.736.058-1.93.06-3.658.156-5.045.294l-.07-.002c-.795.058-1.364.274-1.944.945-.46.493-.835 1.842-.839 2.317l-.035.333a41.2 41.2 0 00-.078.89C.643 6.369.6 7.289.6 8.144v1.949c.004.807.046 1.673.113 2.553.04.506.117 1.31.117 1.321 0 .385.367 1.724.814 2.228.191.223.399.397.623.53.39.233.721.316 1.298.394.102 0 .146.001.214.008.07.007.138.02.207.039z"
                              stroke="#FFF"
                              strokeWidth="1.2"
                            ></path>
                            <path
                              d="M15.81 8.79L8.57 5.05a.384.384 0 00-.38 0c-.115.069-.19.17-.19.305v7.31c0 .134.076.235.19.302a.382.382 0 00.191.034c.076 0 .114 0 .19-.034l7.239-3.57c.114-.068.19-.169.19-.303 0-.135-.076-.236-.19-.304z"
                              fill="#FFF"
                            ></path>
                          </g>
                        </svg>
                      </span>
                      Play full video
                    </div>
                    {/* Actual video element (optional for looped preview) */}
                    <video
                      className="game-card-video__video"
                      playsInline
                      loop
                    />
                    {/* Preview image as background */}
                    <div
                      className="game-card-video-preview game-card-video-preview_button"
                      style={{
                        backgroundImage: `url(${item.background_image})`,
                      }}
                    ></div>
                  </div>
                </div>
              </div>

              {/* Info Section */}
              <div className="game-card-medium__info">
                <div className="game-card-medium__meta">
                  <div className="platforms platforms_medium game-card-medium__platforms">
                    {/* Example usage of your checkAccountType or platform icons */}
                    {checkAccountType(item.platforms.map((p) => p.platform.name))}
                  </div>
                </div>

                <div className="heading heading_2">
                  {/* Link to game details */}
                  <a
                    className="game-card-medium__info__name"
                    href={`/news/game/${item.id}`}
                  >
                    {item.name}
                    {/* Example: show “exceptional” rating icon if rating >= 4.0 */}
                    {item.rating >= 4.0 && (
                      <div
                        className="rating rating_emoji rating_exceptional game-card-medium__info__rating"
                        title="exceptional"
                        role="button"
                        tabIndex={0}
                      >
                        <div className="rating__icon rating__icon_exceptional" />
                      </div>
                    )}
                  </a>
                </div>

                <div className="game-card-buttons">
                  {/* Dropdowns, Add to list, Wish list, etc. */}
                  <div
                    className="dropdown dropdown_card game-card-buttons__dropdown"
                    id="dropdown-container"
                  >
                    <div
                      className="dropdown__button"
                      role="button"
                      tabIndex={0}
                    />
                  </div>
                  <div
                    className="dropdown dropdown_card game-card-buttons__dropdown"
                    id="dropdown-container"
                  >
                    <div
                      className="dropdown__button"
                      role="button"
                      tabIndex={0}
                    />
                  </div>

                  {/* Example add button with count (e.g., total user ratings or some other metric) */}
                  <button
                    className="game-card-button game-card-button_add game-card-button_light"
                    type="button"
                  >
                    <span className="SVGInline game-card-button__icon game-card-button__icon_12 game-card-button__icon_with-offset">
                      <svg
                        className="SVGInline-svg game-card-button__icon-svg game-card-button__icon_12-svg game-card-button__icon_with-offset-svg"
                        xmlns="http://www.w3.org/2000/svg"
                        viewBox="0 0 12 12"
                      >
                        <g fill="#fff" fillRule="evenodd">
                          <rect width="3" height="12" x="4.5" rx=".75"></rect>
                          <rect
                            width="3"
                            height="12"
                            x="4.5"
                            rx=".75"
                            transform="rotate(-90 6 6)"
                          ></rect>
                        </g>
                      </svg>
                    </span>
                    <span className="game-card-button__inner">
                      {item.ratings_count ?? 0}
                    </span>
                  </button>

                  {/* Wishlist button */}
                  <button
                    className="game-card-button game-card-button_wishlist game-card-button_light"
                    type="button"
                  >
                    <span className="SVGInline game-card-button__icon game-card-button__icon_20">
                      <svg
                        className="SVGInline-svg game-card-button__icon-svg game-card-button__icon_20-svg"
                        xmlns="http://www.w3.org/2000/svg"
                        width="30"
                        height="30"
                        viewBox="0 0 30 30"
                      >
                        <path
                          fill="#FFF"
                          d="M25.5 9.846h-4.746a5.87 5.87 0 00.837-.657 3.027 3.027 0 000-4.32c-1.175-1.158-3.223-1.159-4.4 0-.649.639-2.375 3.24-2.137 4.977h-.108c.237-1.738-1.488-4.339-2.138-4.978-1.176-1.158-3.224-1.157-4.4 0a3.028 3.028 0 000 4.321c.205.203.498.429.838.657H4.5A1.487 1.487 0 003 11.314v3.672c0 .405.336.734.75.734h.75v8.812c.004.813.675 1.47 1.5 1.468h18a1.487 1.487 0 001.5-1.468V15.72h.75c.414 0 .75-.329.75-.734v-3.672a1.487 1.487 0 00-1.5-1.468zM9.472 5.904a1.61 1.61 0 011.138-.464c.427 0 .83.164 1.135.464 1.011.995 2.016 3.54 1.667 3.893 0 0-.064.048-.278.048-1.036 0-3.015-1.054-3.662-1.691a1.578 1.578 0 010-2.25zm4.778 18.628H6V15.72h8.25v8.812zm0-10.28H4.5v-2.938h9.75v2.938zm4.005-8.348c.609-.598 1.665-.597 2.273 0a1.578 1.578 0 010 2.25c-.647.637-2.626 1.692-3.662 1.692-.214 0-.278-.047-.279-.049-.348-.354.657-2.898 1.668-3.893zM24 24.532h-8.25V15.72H24v8.812zm1.5-10.28h-9.75v-2.938h9.75v2.938z"
                        ></path>
                      </svg>
                    </span>
                  </button>

                  <div
                    className="dropdown dropdown_card game-card-buttons__dropdown"
                    id="dropdown-container"
                  >
                    <div
                      className="dropdown__button"
                      role="button"
                      tabIndex={0}
                    />
                  </div>

                  {/* “More” button */}
                  <button
                    className="game-card-button game-card-button_more game-card-button_light"
                    type="button"
                  >
                    <span className="SVGInline game-card-button__icon game-card-button__icon_16">
                      <svg
                        className="SVGInline-svg game-card-button__icon-svg game-card-button__icon_16-svg"
                        width="16"
                        height="16"
                        viewBox="0 0 16 16"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          d="M2 6c1.103 0 2 .897 2 2s-.897 2-2 2-2-.897-2-2 .897-2 2-2zm12 0c1.103 0 2 .897 2 2s-.897 2-2 2-2-.897-2-2 .897-2 2-2zM8 6c1.103 0 2 .897 2 2s-.897 2-2 2-2-.897-2-2 .897-2 2-2z"
                          fill="#FFF"
                          fillRule="evenodd"
                        ></path>
                      </svg>
                    </span>
                  </button>
                </div>

                {/* “View more” link, or other info */}
                <ul className="game-card-about game-card-about_normal game-card-about_table">
                  <li className="game-card-about__item game-card-about__item_view-more game-card-about__item_center">
                    <span
                      className="game-card-about__more"
                      role="button"
                      tabIndex={0}
                    >
                      View more
                    </span>
                  </li>
                </ul>

                {/* Example: If you still want to show a quick “hover detail” */}
                {hoveredGameSlug === item.slug && (
                  <div className="game-mini-details">
                    <p>Release: {item.released}</p>
                    <p>
                      Genres:{" "}
                      {item.genres.map((genre) => genre.name).join(", ")}
                    </p>
                    <p>Rating: {item.rating > 0 ? item.rating : item.averageRating}</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}

export default News;
