import React, { useEffect, useState, useRef } from "react";
import { fetchAllNews, searchForGame, getGenres } from "../service/NewsService";
import { fetchGameTrailers } from "../service/GameTrailersFromYoutubeService";
import { AddNewsGamesToGameList, AddToWishList, SaveToCollection } from "../NewsHelper/AddNewsGamesToGameList";
import { useSearchParams, useNavigate } from "react-router-dom";
import "../assests/News.css"; // Make sure your CSS is updated (see below)
import { checkAccountType } from "../utility/CheckAccountType";

function News() {
  const [searchParams] = useSearchParams();
  const selectedGenre = searchParams.get("genre") || "";
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const textRef = useRef("");
  const navigate = useNavigate();
  const username = localStorage.getItem("username");
  const [genres, setGenres] = useState([]);
  const [hoveredGameSlug, setHoveredGameSlug] = useState(null);
  const trailerRef = useRef({});

  useEffect(() => {
    trailerRef.current = trailers;
  }, [trailers]);
  // Holds trailer data for each game, keyed by slug
  const [trailers, setTrailers] = useState({});

  const handleKeyPress = (event) => {
    if (event.key === "Enter") handleSearch();
  };

  const handleSearch = async () => {
    const inputValue = textRef.current.value.trim();
    try {
      if (!inputValue) {
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } else {
        const results = await searchForGame(inputValue);
        setNewsData(results || []);
      }
    } catch (err) {
      setError(err);
    }
    textRef.current.value = "";
  };

  // Navigate to game detail page
  const handleCardClick = (game) => {
    navigate(`/news/game/${game.id}`);
  };

  // Fetch news on mount or when genre changes
  useEffect(() => {
    async function getNews() {
      try {
        setLoading(true);
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    getNews();
  }, [selectedGenre]);

  // Fetch genres
  useEffect(() => {
    async function fetchGenreOptions() {
      try {
        const data = await getGenres();
        setGenres(data);
      } catch (err) {
        console.error("Error fetching genres:", err);
      }
    }
    fetchGenreOptions();
  }, []);

  // Pre-fetch trailers so hover is smooth
  useEffect(() => {
    if (newsData.length > 0) {
      let isMounted = true;

      const fetchTrailersWithThrottle = async () => {
        const newTrailer = {};
        const trailerPromises = newsData.map(async (item) => {
          if (!isMounted) return;
          // Skip if already fetched
          if (trailerRef.current[item.slug] !== undefined) return;
          try {
            const data = await fetchGameTrailers({ gameName: item.name });
            newTrailer[item.slug] = data.items ? data.items[0] : null;
          } catch (error) {
            console.error("Error fetching trailer for", item.name, error);
            newTrailer[item.slug] = null;
          }
        });
        // ...code to wait for trailerPromises and update state...
      };

      fetchTrailersWithThrottle();

      return () => {
        isMounted = false;
      };
    }
  }, [newsData]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div className="news-page">
      {/* You could add a sidebar here if desired */}
      <div className="news-content">
        <h1>News and Trending Games For {username}</h1>

        {/* Genre Filter */}
        <div style={{ marginBottom: "1rem" }}>
          <label htmlFor="genre-select" style={{ marginRight: "0.5rem" }}>
            Filter by Genre:
          </label>
          <select
            id="genre-select"
            value={selectedGenre}
            onChange={(e) =>
              navigate(
                e.target.value ? `/news?genre=${e.target.value}` : "/news"
              )
            }
          >
            <option value="">All Genres</option>
            {genres.map((genre) => (
              <option key={genre.slug} value={genre.slug}>
                {genre.name}
              </option>
            ))}
          </select>
        </div>

        {/* Search Bar */}
        <div className="search-bar">
          <input
            ref={textRef}
            type="text"
            placeholder="Search for Game"
            onKeyDown={handleKeyPress}
          />
          <button onClick={handleSearch}>Search</button>
        </div>

        {/* Game Cards */}
        {newsData.map((item) => {
          const isHovered = hoveredGameSlug === item.slug;
          const trailerData = trailers[item.slug];
          const hasTrailer = trailerData !== undefined && trailerData !== null;

          return (
            <div
              className="game-card"
              key={item.slug}
              onMouseEnter={() => setHoveredGameSlug(item.slug)}
              onMouseLeave={() => setHoveredGameSlug(null)}
            >
              {/* Card Header/Media */}
              <div className="card-media">
                <div
                  className="game-card-video-preview"
                  style={{ backgroundImage: `url(${item.background_image})` }}
                ></div>
                {isHovered && hasTrailer && (
                  <iframe
                    className="card-video"
                    src={`https://www.youtube.com/embed/${trailerData.id.videoId}?autoplay=1&mute=1&start=15`}
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                    title={item.name}
                  ></iframe>
                )}
                {isHovered && (
                  <button
                    className="play-full-video-button"
                    onClick={() => handleCardClick(item)}
                  >
                    Play full video
                  </button>
                )}
              </div>

              {/* Card Body */}
              <div className="card-body">
                {/* Platform icons or text */}
                <div className="platform-icons">
                  {item.platforms.map((p) => (
                    <span key={p.platform.id} className="platform-icon">
                      {p.platform.name}
                    </span>
                  ))}
                </div>

                <h2 className="game-title">{item.name}</h2>

                {/* Show extra info on hover */}
                {isHovered && (
                  <div className="game-extra-info">
                    <p className="release-date">
                      <strong>Release:</strong> {item.released ? item.released : "TBA"}
                    </p>
                    <p className="genres">
                      <strong>Genres:</strong> {item.genres.map((g) => g.name).join(", ")}
                    </p>
                  </div>
                )}

                {/* Example “+304” or other stat */}
                <div className="stats-row">
                  <span className="player-count">+304</span>
                  <span className="emoji-target">🎯</span>
                </div>

                {/* Release date, genres, etc. */}
                <p className="release-date">
                  <strong>Release date:</strong>{" "}
                  {item.released ? item.released : "TBA"}
                </p>
                <p className="genres">
                  <strong>Genres:</strong>{" "}
                  {item.genres.map((g) => g.name).join(", ")}
                </p>

                {/* Example chart position */}
                <p className="chart-position">
                  <strong>Chart:</strong> #1 Top 2026
                </p>

                <p className="rating">
                  <strong>Rating:</strong>{" "}
                  {item.rating > 0 ? item.rating : item.averageRating}
                </p>

                {/* Example: Show more like this */}
                <button
                  className="show-more-button"
                  onClick={() => alert("Show more like this...")}
                >
                  Show more like this
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default News;
