import { FaDesktop, FaXbox, FaPlaystation, FaGamepad } from "react-icons/fa";

export const checkAccountType = (platform) =>{
    if (!Array.isArray(platform)) {
        throw new Error("Expected an array of platforms");
    }
    let platfromCount = {
        pc :0,
        xbox:0,
        playstation:0,
        other:0
    };
    platform.forEach(platform => {
        switch (platform.toLowerCase()) {
        case "pc":
            platfromCount.pc++;
            break;
        case "xbox one":
        case "xbox series s/x":
        case "xbox":
            platfromCount.xbox++;
            break;
        case "playstation 5":
        case "ps4":
        case "ps5":
        case "playstation":
            platfromCount.playstation++;
            break;
        default:
            platfromCount.other++;
            break;
        }
    });
    if(platfromCount > 2) {
        return <FaGamepad />;
    }
    else{
        return (
            <>
                {platfromCount.pc > 0 && <FaDesktop />}
                {platfromCount.xbox > 0 && <FaXbox />}
                {platfromCount.playstation > 0 && <FaPlaystation />}
            </>
        );
    }

}
