// src/components/News.jsx
import React, { useEffect, useState } from 'react';
import { fetchAllNews } from "../service/NewsService";
import { fetchGenres, fetchPlatforms } from "../service/FilterService"; // Assuming you have these

function News() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);

  // Filter states
  const [genres, setGenres] = useState([]);
  const [platforms, setPlatforms] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedPlatform, setSelectedPlatform] = useState('');
  const [searchGame, setSearchGame] = useState(''); // if you plan to add game search later
  const [startDate, setStartDate] = useState(null);   // if date filtering is added
  const [endDate, setEndDate] = useState(null);

  // Load dynamic filter options on mount
  useEffect(() => {
    const fetchFilters = async () => {
      try {
        const fetchedGenres = await fetchGenres();
        setGenres(fetchedGenres);
        const fetchedPlatforms = await fetchPlatforms();
        setPlatforms(fetchedPlatforms);
      } catch (error) {
        console.error("Error fetching filter options:", error);
      }
    };
    fetchFilters();
  }, []);

  // Fetch news whenever filters change
  useEffect(() => {
    const fetchFilteredNews = async () => {
      setLoading(true);
      try {
        // You can format dates here if you add date filtering
        const data = await fetchAllNews({
          platform: selectedPlatform,
          genre: selectedGenre,
          // game: searchGame, startDate, endDate can be added if needed
        });
        setNews(data);
      } catch (error) {
        console.error("Error fetching news:", error);
      }
      setLoading(false);
    };

    fetchFilteredNews();
  }, [selectedGenre, selectedPlatform, searchGame, startDate, endDate]);

  return (
    <div className="news">
      <h1>News Page</h1>
      <div className="filters">
        <label>
          Genre:
          <select value={selectedGenre} onChange={e => setSelectedGenre(e.target.value)}>
            <option value="">All Genres</option>
            {genres.map(genre => (
              <option key={genre.id} value={genre.slug}>
                {genre.name}
              </option>
            ))}
          </select>
        </label>
        <label>
          Platform:
          <select value={selectedPlatform} onChange={e => setSelectedPlatform(e.target.value)}>
            <option value="">All Platforms</option>
            {platforms.map(platform => (
              <option key={platform.id} value={platform.slug}>
                {platform.name}
              </option>
            ))}
          </select>
        </label>
        {/* Additional filter inputs (like search game, dates) can go here */}
      </div>

      {loading ? (
        <p>Page is loading...</p>
      ) : news.length > 0 ? (
        news.map((newsItem, index) => (
          <div key={index} className="news-item">
            <h2>{newsItem.name}</h2>
            {newsItem.background_image && (
              <img src={newsItem.background_image} alt={newsItem.name} />
            )}
            <p>Released: {newsItem.released}</p>
            {newsItem.genres && (
              <div className="genres">
                {newsItem.genres.map((g, idx) => (
                  <span key={idx}>{g.name}</span>
                ))}
              </div>
            )}
          </div>
        ))
      ) : (
        <p>No news found.</p>
      )}
    </div>
  );
}

export default News;
