import React, { useEffect, useState, useRef } from "react";
import { fetchAllNews, searchForGame, getGenres } from "../service/NewsService";
import { fetchGameTrailers } from "../service/GameTrailersFromYoutubeService";
import { AddNewsGamesToGameList, AddToWishList, SaveToCollection } from "../NewsHelper/AddNewsGamesToGameList";
import { useSearchParams, useNavigate } from "react-router-dom";
import "../assests/News.css";
import { checkAccountType } from "../utility/CheckAccountType";

function News() {
  const [searchParams] = useSearchParams();
  const selectedGenre = searchParams.get('genre') || '';
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const textRef = useRef("");
  const navigate = useNavigate();
  const username = localStorage.getItem("username");
  const [genres, setGenres] = useState([]);
  const [hoveredGameSlug, setHoveredGameSlug] = useState(null);

  // These state objects store trailer data and loading status per game (keyed by game slug)
  const [trailers, setTrailers] = useState({});
  const [trailerLoading, setTrailerLoading] = useState({});

  const handleKeyPress = (event) => {
    if (event.key === "Enter") handleSearch();
  };

  const handleImageClick = (game) => {
    navigate(`/news/game/${game.id}`);
  };

  const handleSearch = async () => {
    const inputValue = textRef.current.value.trim();
    try {
      if (!inputValue) {
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } else {
        const results = await searchForGame(inputValue);
        setNewsData(results || []);
      }
    } catch (err) {
      setError(err);
    }
    textRef.current.value = "";
  };

  useEffect(() => {
    async function getNews() {
      try {
        setLoading(true);
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    getNews();
  }, [selectedGenre]);

  useEffect(() => {
    async function fetchGenreOptions() {
      try {
        const data = await getGenres();
        setGenres(data);
      } catch (err) {
        console.error("Error fetching genres:", err);
      }
    }
    fetchGenreOptions();
  }, []);

  // When a game is hovered, fetch its trailer if not already cached
  const handleMouseEnter = async (item) => {
    setHoveredGameSlug(item.slug);
    if (trailers[item.slug] === undefined) {  // Trailer not fetched yet
      setTrailerLoading((prev) => ({ ...prev, [item.slug]: true }));
      try {
        const data = await fetchGameTrailers({ gameName: item.name });
        const trailer = (data.items && data.items.length > 0) ? data.items[0] : null;
        setTrailers((prev) => ({ ...prev, [item.slug]: trailer }));
      } catch (err) {
        console.error("Error fetching trailer for", item.name, err);
        setTrailers((prev) => ({ ...prev, [item.slug]: null }));
      } finally {
        setTrailerLoading((prev) => ({ ...prev, [item.slug]: false }));
      }
    }
  };

  const handleMouseLeave = () => {
    setHoveredGameSlug(null);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <>
      <h1>News and Trending Games For {username}</h1>
      <div style={{ marginBottom: "1rem" }}>
        <label htmlFor="genre-select" style={{ marginRight: "0.5rem" }}>
          Filter by Genre:
        </label>
        <select
          id="genre-select"
          value={selectedGenre}
          onChange={(e) =>
            navigate(e.target.value ? `/news?genre=${e.target.value}` : "/news")
          }
        >
          <option value="">All Genres</option>
          {genres.map((genre) => (
            <option key={genre.slug} value={genre.slug}>
              {genre.name}
            </option>
          ))}
        </select>
      </div>

      <div className="search-bar">
        <input
          ref={textRef}
          type="text"
          placeholder="Search for Game"
          onKeyDown={handleKeyPress}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {newsData.map((item) => (
        <div className="game-item" key={item.slug}>
          <div
            className="video-container"
            onMouseEnter={() => handleMouseEnter(item)}
            onMouseLeave={handleMouseLeave}
            onClick={() => handleImageClick(item)}
          >
            {hoveredGameSlug === item.slug ? (
              trailerLoading[item.slug] ? (
                <p>Loading trailer...</p>
              ) : (
                trailers[item.slug] ? (
                  <iframe
                    width="300"
                    height="200"
                    src={`https://www.youtube.com/embed/${trailers[item.slug].id.videoId}?autoplay=1&mute=1`}
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                    title={item.name}
                  ></iframe>
                ) : (
                  <img
                    src={item.background_image}
                    alt={item.name}
                    style={{ width: "300px", height: "auto", cursor: "pointer" }}
                  />
                )
              )
            ) : (
              <img
                src={item.background_image}
                alt={item.name}
                style={{ width: "300px", height: "auto", cursor: "pointer" }}
              />
            )}
          </div>
          <div>
            {checkAccountType(item.platforms.map((p) => p.platform.name))}
          </div>
          <h2>{item.name}</h2>
          {hoveredGameSlug === item.slug && (
            <div className="game-mini-details">
              <p>Release: {item.released}</p>
              <div className="genre-for-game">
                <p>
                  Genres:{" "}
                  {item.genres.map((genre) => (
                    <span key={genre.slug}>{genre.name} </span>
                  ))}
                </p>
              </div>
              <div className="game-rating">
                <p>Rating: </p>
                {item.rating > 0 ? item.rating : item.averageRating}
              </div>
            </div>
          )}
          <div className="btn-container">
            {/* Buttons can be added here if needed */}
          </div>
        </div>
      ))}
    </>
  );
}

export default News;
