import React, { useEffect, useState, useRef } from "react";
import { fetchAllNews, searchForGame, getGenres } from "../service/NewsService";
import { fetchGameTrailers } from "../service/GameTrailersFromYoutubeService";
import { useSearchParams, useNavigate } from "react-router-dom";
import "../assests/News.css";

function News() {
  const [searchParams] = useSearchParams();
  const selectedGenre = searchParams.get("genre") || "";
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const textRef = useRef("");
  const navigate = useNavigate();
  const username = localStorage.getItem("username");
  const [genres, setGenres] = useState([]);
  const [hoveredGameSlug, setHoveredGameSlug] = useState(null);
  const [trailers, setTrailers] = useState({}); // { [slug]: trailerData }

  useEffect(() => {
    async function getNews() {
      try {
        setLoading(true);
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    getNews();
  }, [selectedGenre]);

  useEffect(() => {
    async function fetchGenreOptions() {
      try {
        const data = await getGenres();
        setGenres(data);
      } catch (err) {
        console.error("Error fetching genres:", err);
      }
    }
    fetchGenreOptions();
  }, []);

  // Handle search
  const handleSearch = async () => {
    const inputValue = textRef.current.value.trim();
    try {
      if (!inputValue) {
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } else {
        const results = await searchForGame(inputValue);
        setNewsData(results || []);
      }
    } catch (err) {
      setError(err);
    }
    textRef.current.value = "";
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") handleSearch();
  };

  const handleCardClick = (game) => {
    navigate(`/news/game/${game.id}`);
  };

  // Hover logic to fetch trailer once
  const handleMouseEnter = async (item) => {
    setHoveredGameSlug(item.slug);
    if (trailers[item.slug] !== undefined) return; // already fetched or failed

    try {
      const data = await fetchGameTrailers({ gameName: item.name });
      const trailer = data.items?.[0] || null;
      setTrailers((prev) => ({ ...prev, [item.slug]: trailer }));
    } catch (err) {
      console.error("Error fetching trailer for", item.name, err);
      setTrailers((prev) => ({ ...prev, [item.slug]: null }));
    }
  };

  const handleMouseLeave = () => {
    setHoveredGameSlug(null);
  };

  // If you want to change the genre by clicking in the sidebar:
  const handleGenreClick = (genreSlug) => {
    navigate(genreSlug ? `/news?genre=${genreSlug}` : "/news");
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div className="news-page">
      {/* Sidebar for genres */}
      {/* <div className="news-sidebar">
        <h2>Genres</h2>
        <ul className="genre-list">
          <li
            className={!selectedGenre ? "active-genre" : ""}
            onClick={() => handleGenreClick("")}
          >
            All Genres
          </li>
          {genres.map((genre) => (
            <li
              key={genre.slug}
              className={selectedGenre === genre.slug ? "active-genre" : ""}
              onClick={() => handleGenreClick(genre.slug)}
            >
              {genre.name}
            </li>
          ))}
        </ul>
      </div> */}

      {/* Main content (search bar + cards) */}
      <div className="news-main">
        <h1>Trending Games For {username}</h1>

        {/* Search Bar */}
        <div className="search-bar">
          <input
            ref={textRef}
            type="text"
            placeholder="Search for Game"
            onKeyDown={handleKeyPress}
          />
          <button onClick={handleSearch}>Search</button>
        </div>

        {/* Cards Grid */}
        <div className="news-content">
          {newsData.map((item) => {
            const isHovered = hoveredGameSlug === item.slug;
            const trailerData = trailers[item.slug];
            const hasTrailer = trailerData !== undefined && trailerData !== null;

            return (
              <div
                className="game-card"
                key={item.slug}
                onMouseEnter={() => handleMouseEnter(item)}
                onMouseLeave={handleMouseLeave}
              >
                <div className="card-media">
                  {isHovered && hasTrailer ? (
                    <iframe
                      src={`https://www.youtube.com/embed/${trailerData.id.videoId}?autoplay=1&mute=1&start=15`}
                      frameBorder="0"
                      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                      allowFullScreen
                      title={item.name}
                    ></iframe>
                  ) : (
                    <img
                      src={item.background_image}
                      alt={item.name}
                      className="card-image"
                    />
                  )}

                  {isHovered && (
                    <button
                      className="play-full-video-button"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleCardClick(item);
                      }}
                    >
                      Play full video
                    </button>
                  )}
                </div>

                <div className="card-body" onClick={() => handleCardClick(item)}>
                  <h2>{item.name}</h2>
                  {/* Additional info if you want */}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default News;
