import React, { useEffect, useState, useRef } from "react";
import { fetchAllNews, getGenres, searchForGame } from "../service/NewsService";
import { AddNewsGamesToGameList , AddToWishList, SaveToCollection} from "../NewsHelper.js/AddNewsGamesToGameList";
import '../assests/News.css';
function News() {
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("");
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // useRef to hold the search input element
  const textRef = useRef("");


  // Function to search when user presses Enter
  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  // Function to handle game search
  const handleSearch = async () => {
    const inputValue = textRef.current.value.trim(); // Trim whitespace
    // If input is empty, fetch default news
    if (!inputValue) {
      try {
        const genreFilter = selectedGenre ? selectedGenre : null;
        const data = await fetchAllNews(null, genreFilter);
        setNewsData(data);
      } catch (error) {
        console.error(error);
        setError(error);
      } finally {
        // Clear the input text after search
        textRef.current.value = "";
      }
      return;
    }
    console.log("Searching for:", inputValue); // Debugging
    try {
      const results = await searchForGame(inputValue);
      setNewsData(results || []);
    } catch (error) {
      console.error(error);
      setError(error);
    } finally {
      // Clear the input text after search
      textRef.current.value = "";
    }
  };

  // Fetch genres when component mounts
  useEffect(() => {
    async function fetchGenreOptions() {
      try {
        const data = await getGenres();
        setGenres(data);
      } catch (err) {
        console.error("Error fetching genres:", err);
      }
    }
    fetchGenreOptions();
  }, []);

  // Fetch default news when selectedGenre changes
  useEffect(() => {
    async function getNews() {
      try {
        setLoading(true);
        const genreFilter = selectedGenre ? selectedGenre : null;
        const data = await fetchAllNews(null, genreFilter);
        setNewsData(data);
      } catch (err) {
        console.error("Error fetching news:", err);
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    getNews();
  }, [selectedGenre]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div>
      <h1>News</h1>

      {/* Dropdown for genres */}
      <div style={{ marginBottom: "1rem" }}>
        <label htmlFor="genre-select" style={{ marginRight: "0.5rem" }}>
          Filter by Genre:
        </label>
        <select
          id="genre-select"
          value={selectedGenre}
          onChange={(e) => setSelectedGenre(e.target.value)}
        >
          <option value="">All Genres</option>
          {genres.map((genre) => (
            <option key={genre.slug} value={genre.slug}>
              {genre.name}
            </option>
          ))}
        </select>
      </div>

      {/* Search bar */}
      <div className="search-bar">
        <input
          ref={textRef} // Attach ref to input
          type="text"
          placeholder="Search for Game"
          onKeyDown={handleKeyPress} // Detect Enter key press
          style={{ marginBottom: "1rem" }}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Display search results or default news */}
      <div>
        {newsData.map((item) => (
          <div
            key={item.slug}
            style={{
              border: "1px solid #ccc",
              margin: "1rem",
              padding: "1rem",
            }}
          >
            <h2>{item.name}</h2>
            <p>Released: {item.released}</p>
            <button onClick={() => AddNewsGamesToGameList({item})}>Add to Game List </button>
            <button onClick={() => AddToWishList({item})}>Add to Wishlist</button>
            <button onClick={() => SaveToCollection({item})}>Save to Collection</button>
            <img
              src={item.background_image}
              alt={item.name}
              style={{ width: "300px", height: "auto" }}
            />
            <div>
              <strong>Platforms:</strong>
              <ul>
                {item.platforms.map((p) => (
                  <li key={p.platform.slug}>{p.platform.name}</li>
                ))}
              </ul>
            </div>
            <div>
              <strong>Genres:</strong>
              <ul>
                {item.genres.map((g) => (
                  <li key={g.slug}>{g.name}</li>
                ))}
              </ul>
            </div>
            <div>
              <strong>Screenshots:</strong>
              <div style={{ display: "flex", gap: "0.5rem", overflowX: "auto" }}>
                {item.short_screenshots.map((shot, index) => (
                  <img
                    key={index}
                    src={shot.image}
                    alt={`${item.name} screenshot ${index + 1}`}
                    style={{ width: "150px", height: "auto" }}
                  />
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default News;
