import React, { useEffect, useState } from "react";
import { fetchAllNews, getGenres } from "../service/NewsService";
const [genres, setGenres] = useState([]);
const [selectedGenre, setSelectedGenre] = useState("");
// put the genres in a list - 

const fetchGenres = async () => {
    const genres = await getGenres();
    setGenres(genres);
}

function News() {
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function getNews() {
      try {
        const data = await fetchAllNews();
        setNewsData(data);
      } catch (err) {
        console.error(err);
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    getNews();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div>
      {newsData.map((item) => (
        <div key={item.slug} style={{ border: "1px solid #ccc", margin: "1rem", padding: "1rem" }}>
          <h2>{item.name}</h2>
          <p>Released: {item.released}</p>
          <img
            src={item.background_image}
            alt={item.name}
            style={{ width: "300px", height: "auto" }}
          />
          <div>
            <strong>Platforms:</strong>
            <ul>
              {item.platforms.map((p) => (
                <li key={p.platform.slug}>{p.platform.name}</li>
              ))}
            </ul>
          </div>
          <div>
            <strong>Genres:</strong>
            <ul>
              {item.genres.map((g) => (
                <li key={g.name}>{g.name}</li>
              ))}
            </ul>
          </div>
          <div>
            <strong>Screenshots:</strong>
            <div style={{ display: "flex", gap: "0.5rem", overflowX: "auto" }}>
              {item.short_screenshots.map((shot, index) => (
                <img
                  key={index}
                  src={shot.image}
                  alt={`${item.name} screenshot ${index + 1}`}
                  style={{ width: "150px", height: "auto" }}
                />
              ))}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default News;
