import { FaDesktop, FaXbox, FaPlaystation, FaGamepad } from "react-icons/fa";

export const checkAccountType = (platform) =>{
    if (!Array.isArray(platform)) {
        throw new Error("Expected an array of platforms");
    }
    let platfromCount = {
        pc :0,
        xbox:0,
        playstation:0,
        other:0
    };
    platform.forEach(platform => {
        switch (platform.toLowerCase()) {
        case "pc":
            platfromCount.pc++;
            break;
        case "xbox one":
        case "xbox series s/x":
        case "xbox":
            platfromCount.xbox++;
            break;
        case "playstation 5":
        case "ps4":
        case "ps5":
        case "playstation":
            platfromCount.playstation++;
            break;
        default:
            platfromCount.other++;
            break;
        }
    });
    const icons = [];

    if (platfromCount.pc > 0) {
        icons.push(<FaDesktop key="pc" style={{ marginRight: "5px" }} />);
    }
    if (platfromCount.xbox > 0) {
        icons.push(<FaXbox key="xbox" style={{ marginRight: "5px" }} />);
    }
    if (platfromCount.playstation > 0) {
        icons.push(<FaPlaystation key="playstation" style={{ marginRight: "5px" }} />);
    }
    if (platfromCount.other > 0) {
        icons.push(<FaGamepad key="other" style={{ marginRight: "5px" }} />);
    }
    return icons;
}
