// news page - fetch news based on the genre, platform, game, and date
// filter by genre, platform, game, and date
import React, { useState } from 'react';
import { fetchAllNews } from "../service/NewsService";

function News(){
    const [news, setNews] = React.useState([]);
    const [loading, setLoading] = React.useState(true);
    // fetch all news -
    const fetchAllNews = async () => { 
        try{
            const data = await fetchAllNews();
            setNews(data);
            setLoading(false);
        }catch(error){
            console.error(error);
            setLoading(false);
        }
    };
    return(
        <div className="news">
            <h1>News Page</h1>
        </div>
    );
}

export default News;
