import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchGameDetails } from '../service/NewsService';
import { AddNewsGamesToGameList, AddToWishList, SaveToCollection } from '../NewsHelper/AddNewsGamesToGameList';
import { fetchGameTrailers } from '../service/GameTrailersFromYoutubeService';
function GameDetail() {
  const { id } = useParams();
  const [game, setGame] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // get the game trailer = 
    const [trailers, setTrailers] = useState([]);

    const fetchTrailers = async ({gameName}) => {
        await fetchGameTrailers({gameName})
    }
  useEffect(() => {
    async function fetchGame() {
      try {
        const data = await fetchGameDetails({id});
        setGame(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    fetchGame();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div className="game-detail">
      <h1>{game.name}</h1>
      <div className='btn-container'>
        <button 
            className='my-games-button'
            onClick={() => AddNewsGamesToGameList({id : game.id})}
        >
            Add to My Games
            <span className='plus-icon' />
        </button>
        <button 
            className='wishlist-button'
            onClick={() => AddToWishList(item)}
        >   
            Add to Wishlist
            <span className='gift-icon' />
        </button>
        <button 
            className='collection-button'
            onClick={() => SaveToCollection(item)}
        >
            Add to Collection
            <span className='folder-icon' />
        </button>
      </div>
      {
        game.ratings && (
          <div>
            <p>{game.ratings[0].title}</p>
            <p> Players added to their libarary: {game.ratings[0].count}</p>
            </div>
        )
      }
      <div>
        <h3>Platforms</h3>
        <ul>
          {game.platforms?.map((p) => (
            <li key={p.platform.id}>{p.platform.name}</li>
          ))}
        </ul>
      </div>
      <div>
        <h3>Genres</h3>
        <ul>
          {game.genres?.map((g) => (
            <li key={g.id}>{g.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default GameDetail;
