import React, { useState, useEffect } from "react";
import { getGameClips } from "../service/XboxGameClips";

function ClipsPage() {
    const [gameClips, setGameClips] = useState([]); // State to store game clips
    const [isFetchingClips, setIsFetchingClips] = useState(true); // State for loading    
    const [error, setError] = useState(null); // State for error handling

    const fetchGameClips = async () => {
        try {
            const clips = await getGameClips();
            if (clips.length === 0) {
                setGameClips([]);
            } else {
                setGameClips(clips);
            }
        } catch (err) {
            console.error("Error fetching game clips:", err);
            setError("Failed to load game clips. Please try again.");
        } finally {
            setIsFetchingClips(false);
        }
    };

    useEffect(() => {
        fetchGameClips();
    }, []);

    return (
        <div className="clips-container">
            <h2>Xbox Game Clips</h2>
            {isFetchingClips ? (
                <p>Loading game clips...</p>
            ) : error ? (
                <p>{error}</p>
            ) : gameClips.length === 0 ? (
                <p>No game clips found.</p>
            ) : (
                <div className="clips-grid">
                    {gameClips.map((clip) => (
                        <div key={clip.gameClipId} className="clip-card">
                            <h1>{clip.titleName || "Untitled Clip"}</h1>
                            <p>Recorded on: {new Date(clip.dateRecorded).toLocaleDateString()}</p>
                            {/* Display thumbnails */}
                            {clip.thumbnails?.map((thumbnail, index) => (
                                <img
                                    src={thumbnail.uri}
                                    alt="Game clip thumbnail"
                                    key={index}
                                    className="clip-thumbnail"
                                />
                            ))}
                            {/* Display video clips */}
                            {clip.gameClipUris?.map((uri, index) => (
                                <video
                                    src={uri.uri}
                                    controls
                                    key={index}
                                    className="clip-video"
                                >
                                    Your browser does not support the video tag.
                                </video>
                            ))}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default ClipsPage;
