import React, { useState } from "react";

function App() {
  const [authUrl, setAuthUrl] = useState("");
  const [accessToken, setAccessToken] = useState("");

  // Fetch the Xbox login URL from the backend
  const fetchLoginUrl = async () => {
    try {
      const response = await fetch("http://localhost:8080/auth/login");
      if (!response.ok) {
        throw new Error(`Error: ${response.status} ${response.statusText}`);
      }
      const url = await response.text(); // The backend sends the Xbox login URL
      setAuthUrl(url);
      window.location.href = url; // Redirect to the Xbox login URL
    } catch (error) {
      console.error("Error fetching login URL:", error);
    }
  };

  // Handle the callback from Xbox
  const handleCallback = async () => {
    const params = new URLSearchParams(window.location.search);
    const code = params.get("code"); // Get the 'code' parameter from the URL

    if (code) {
      try {
        const response = await fetch(`http://localhost:8080/auth/callback?code=${code}`);
        if (!response.ok) {
          throw new Error(`Error: ${response.status} ${response.statusText}`);
        }
        const data = await response.json();
        setAccessToken(data.access_token); // Display the access token
      } catch (error) {
        console.error("Error sending token to server:", error);
      }
    }
  };

  React.useEffect(() => {
    handleCallback();
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>GamerHub Xbox Login</h1>
      {!accessToken ? (
        <button onClick={fetchLoginUrl} style={{ padding: "10px 20px", fontSize: "16px" }}>
          Login with Xbox
        </button>
      ) : (
        <div>
          <h2>Access Token:</h2>
          <p>{accessToken}</p>
        </div>
      )}
    </div>
  );
}

export default App;
