import React, { useEffect, useState } from "react";

function App() {
  const [gamertagInfo, setGamertagInfo] = useState(null);

  useEffect(() => {
    // Extract the authorization code from the URL after login redirect
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get("code");

    // If the code exists, send it to the backend to handle token exchange
    if (code) {
      fetch("http://localhost:8080/api/auth/callback?code=" + code, {
        credentials: "include",
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          console.log("Backend response for token exchange:", data);
          // Fetch the profile data from the backend
          fetch("http://localhost:8080/api/xbox/profile/getProfile")
            .then((profileResponse) => {
              if (!profileResponse.ok) {
                throw new Error(
                  `HTTP error fetching profile! Status: ${profileResponse.status}`
                );
              }
              return profileResponse.json();
            })
            .then((profileData) => {
              console.log("Profile Data:", profileData);
              setGamertagInfo(profileData);
            })
            .catch((error) => {
              console.error("Error fetching profile:", error);
            });
        })
        .catch((error) => {
          console.error("Error during token exchange:", error);
        });
    }
  }, []);

  const handleLogin = () => {
    // Redirect to login endpoint to initiate the OAuth process
    window.location.href = "http://localhost:8080/api/auth/login";
  };

  // const testProfile = async => {
  //   try{
  //     const response = data await("http://localhost:8080/xbox/profile/getProfile,
  //       Headers: authorization -
  //   }
  // }

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Welcome to GamerHub</h1>
      {gamertagInfo ? (
        <div>
          <h2>Gamertag: {gamertagInfo.gamertag}</h2>
          <p>Display Name: {gamertagInfo.gameDisplayName}</p>
          <img
            src={gamertagInfo.appDisplayPicRaw}
            alt="Profile Pic"
            style={{ borderRadius: "50%", width: "100px", height: "100px" }}
          />
        </div>
      ) : (
        <button
          onClick={handleLogin}
          style={{
            padding: "10px 20px",
            fontSize: "16px",
            cursor: "pointer",
            backgroundColor: "#0078D7",
            color: "#ffffff",
            border: "none",
            borderRadius: "5px",
          }}
        >
          Login with Xbox
        </button>
      )}
    </div>
  );
}

export default App;
