import React, { useEffect, useState, useRef } from "react";
import { fetchAllNews, searchForGame, getGenres } from "../service/NewsService";
import { useSearchParams, useNavigate } from "react-router-dom";
import "../assests/News.css";
import { checkAccountType } from "../utility/CheckAccountType";
import { fetchGameTrailers } from "../service/GameTrailersFromYoutubeService";

function News() {
  const [searchParams] = useSearchParams();
  const selectedGenre = searchParams.get("genre") || "";
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const textRef = useRef("");
  const navigate = useNavigate();
  const username = localStorage.getItem("username");

  const [genres, setGenres] = useState([]);
  const [hoveredGameSlug, setHoveredGameSlug] = useState(null);
  const [hoveredGameName, setHoveredGameName] = useState(null);
  const [gameTrailers, setGameTrailers] = useState();
  const gameTrailersRef = useRef({}); // Use ref to store cached trailers

  // --- EVENT HANDLERS ---

  // When the user presses Enter, perform a search.
  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  // On clicking the image, optionally fetch trailer (if needed) then navigate.
  const handleImageClick = async (game) => {
    await fetchTrailersForGame({ gameName: game.name });
    navigate(`/news/game/${game.id}`);
  };

  // On mouse enter, just set the hovered game slug and name.
  const handleMouseEnter = (game) => {
    setHoveredGameSlug(game.slug);
    setHoveredGameName(game.name);
  };

  // On mouse leave, clear hovered state.
  const handleMouseLeave = () => {
    setHoveredGameSlug(null);
    setHoveredGameName(null);
  };

  // Search handler for games.
  const handleSearch = async () => {
    const inputValue = textRef.current.value.trim();
    try {
      if (!inputValue) {
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } else {
        const results = await searchForGame(inputValue);
        setNewsData(results || []);
      }
    } catch (err) {
      setError(err);
    }
    textRef.current.value = "";
  };

  // --- useEffect FOR HOVERED GAME NAME ---
  // This runs whenever hoveredGameName changes to a non-null value.
  useEffect(() => {
    if (!hoveredGameName) return;

    const fetchTrailers = async () => {
      if (gameTrailersRef.current[hoveredGameName]) {
        setGameTrailers(gameTrailersRef.current[hoveredGameName]);
        return;
      }

      try {
        const data = await fetchGameTrailers({ gameName: hoveredGameName });
        if (data?.items && data.items.length > 0) {
          gameTrailersRef.current[hoveredGameName] = data.items;
          setGameTrailers(data.items);
          console.log("Fetched trailers");
        } else {
          setGameTrailers([]);
          console.log("No trailers found");
        }
      } catch (error) {
        console.error("Error fetching trailer:", error);
        setGameTrailers([]);
      }
    };

    fetchTrailers();
  }, [hoveredGameName]);

  // --- useEffect FOR NEWS & GENRES ---

  useEffect(() => {
    async function getNews() {
      try {
        setLoading(true);
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    getNews();
  }, [selectedGenre]);

  useEffect(() => {
    async function fetchGenreOptions() {
      try {
        const data = await getGenres();
        setGenres(data);
      } catch (err) {
        console.error("Error fetching genres:", err);
      }
    }
    fetchGenreOptions();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <>
      <h1>News and Trending Games For {username}</h1>
      <div style={{ marginBottom: "1rem" }}>
        <label htmlFor="genre-select" style={{ marginRight: "0.5rem" }}>
          Filter by Genre:
        </label>
        <select
          id="genre-select"
          value={selectedGenre}
          onChange={(e) =>
            navigate(e.target.value ? `/news?genre=${e.target.value}` : "/news")
          }
        >
          <option value="">All Genres</option>
          {genres.map((genre) => (
            <option key={genre.slug} value={genre.slug}>
              {genre.name}
            </option>
          ))}
        </select>
      </div>

      <div className="search-bar">
        <input
          ref={textRef}
          type="text"
          placeholder="Search for Game"
          onKeyDown={handleKeyPress}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      <div className="news-grid">
        {newsData.map((item) => (
          <div className="game-item" key={item.slug || item.id}>
            <div className="card-media">
              <img
                src={item.background_image}
                alt={item.name}
                style={{ width: "300px", height: "auto" }}
                onClick={() => handleImageClick(item)}
                onMouseEnter={() => handleMouseEnter(item)}
                onMouseLeave={handleMouseLeave}
              />
              {hoveredGameSlug === item.slug && gameTrailers.length > 0 && (
                <iframe
                  className="card-video"
                  src={`https://www.youtube.com/embed/${gameTrailers[0].id.videoId}?start=10&autoplay=1&mute=1`}
                  allow="autoplay"
                ></iframe>
              )}
            </div>
            <div>
              {checkAccountType(
                item.platforms.map((p) => p.platform.name)
              )}
            </div>
            <h2>{item.name}</h2>
            {hoveredGameSlug === item.slug && (
              <div className="game-mini-details">
                <p>Release: {item.released}</p>
                <div className="genre-for-game">
                  <p>
                    Genres:{" "}
                    {item.genres.map((genre, index) => (
                      <span key={index} style={{ marginRight: "0.5rem" }}>
                        {genre.name}
                      </span>
                    ))}
                  </p>
                </div>
                <div className="game-rating">
                  <p>Rating: </p>
                  {item.rating > 0 ? item.rating : item.averageRating}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </>
  );
}

export default News;
