import React, { useEffect } from "react";
import { getAllNews } from '../service/PostNewsService';

function CommunityPage() {
  const [news, setNews] = React.useState([]);
  const [isFetching, setFetching] = React.useState(true);
  const [error, setError] = React.useState(null);

  useEffect(() => {
    const fetchNews = async () => {
      const result = await getAllNews();
      if(result){
          setNews(result.data);
          setFetching(false);
      }else {
        setError("Failed to fetch news. Please try again later.");
      }
    };
    fetchNews();
  }, []);
  return (
    <div>
      <h1>Community Page</h1>
      {isFetching ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <ul>
          {news.map((newsItem, index) => (
            <li key={index} style={{ marginBottom: "20px" }}>
              <h3>{newsItem.contentText}</h3>
              <p>Shared by: {newsItem.username}</p>
              <p>News ID: {newsItem.sharedNewsId}</p>
              {newsItem.name && (
                <>
                  <h4>{newsItem.name}</h4>
                  <p>Released: {newsItem.released}</p>
                  <img
                    src={newsItem.backgroundImage}
                    alt={newsItem.name}
                    style={{ width: "200px", height: "auto" }}
                  />
                </>
              )}
              <p>Time Shared: {new Date(newsItem.timeShared).toLocaleString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default CommunityPage;
