// pages/NewsList.jsx
import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import { fetchAllNews, getGenres, searchForGame } from "../service/NewsService";
import {
  AddNewsGamesToGameList,
  AddToWishList,
  SaveToCollection
} from "../NewsHelper/AddNewsGamesToGameList";
import "../assests/News.css";

function NewsList() {
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("");
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const textRef = useRef("");

  // Similar to your original code but simplified
  const handleSearch = async () => {
    const inputValue = textRef.current.value.trim();
    if (!inputValue) {
      try {
        setLoading(true);
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    } else {
      try {
        const results = await searchForGame(inputValue);
        setNewsData(results || []);
      } catch (err) {
        setError(err);
      }
    }
    textRef.current.value = "";
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  useEffect(() => {
    async function loadGenres() {
      try {
        const data = await getGenres();
        setGenres(data);
      } catch (err) {
        console.error("Error fetching genres:", err);
      }
    }
    loadGenres();
  }, []);

  useEffect(() => {
    async function loadNews() {
      try {
        setLoading(true);
        const data = await fetchAllNews(null, selectedGenre || null);
        setNewsData(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    loadNews();
  }, [selectedGenre]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return (
    <div>
      {/* Filter by Genre */}
      <div style={{ marginBottom: "1rem" }}>
        <label htmlFor="genre-select" style={{ marginRight: "0.5rem" }}>
          Filter by Genre:
        </label>
        <select
          id="genre-select"
          value={selectedGenre}
          onChange={(e) => setSelectedGenre(e.target.value)}
        >
          <option value="">All Genres</option>
          {genres.map((genre) => (
            <option key={genre.slug} value={genre.slug}>
              {genre.name}
            </option>
          ))}
        </select>
      </div>

      {/* Search Bar */}
      <div className="search-bar">
        <input
          ref={textRef}
          type="text"
          placeholder="Search for Game"
          onKeyDown={handleKeyPress}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Display Games List */}
      {newsData.map((item) => (
        <div className="game-item" key={item.slug}>
          {/* Link to the detail page */}
          <Link to={`/news/${item.id}`}>
            <h2>{item.name}</h2>
          </Link>
          <p>Released: {item.released}</p>

          <div className="btn-container">
            <button
              className="my-games-button"
              onClick={() => AddNewsGamesToGameList({ id: item.id })}
            >
              Add to My games
            </button>
            <button
              className="wishlist-button"
              onClick={() => AddToWishList({ item })}
            >
              Add to Wishlist
            </button>
            <button
              className="collection-button"
              onClick={() => SaveToCollection({ item })}
            >
              Save to Collection
            </button>
          </div>

          {/* Small preview image */}
          <img
            src={item.background_image}
            alt={item.name}
            style={{ width: "300px", height: "auto" }}
          />

          <div>
            <strong>Platforms:</strong>
            <ul>
              {item.platforms.map((p) => (
                <li key={p.platform.slug}>{p.platform.name}</li>
              ))}
            </ul>
          </div>
          <div>
            <strong>Genres:</strong>
            <ul>
              {item.genres.map((g) => (
                <li key={g.slug}>{g.name}</li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}

export default NewsList;
