import React, { useState, useEffect } from 'react';
import { PublicClientApplication } from '@azure/msal-browser';

// MSAL Configuration
const msalConfig = {
  auth: {
    clientId: "0a23b968-9e79-4cba-b337-862adab7a8e2", // Replace with your Azure AD app's client ID
    authority: "https://login.microsoftonline.com/consumers/", // Ensure this is correct
    redirectUri: "http://localhost:3000", // Replace with your application's redirect URI
  },
  cache: {
    cacheLocation: "sessionStorage",
    storeAuthStateInCookie: false,
  },
};

const LoginWithMicrosoft = () => {
  const [msalInstance, setMsalInstance] = useState(null);
  const [xboxToken, setXboxToken] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const initializeMsal = () => {
      const instance = new PublicClientApplication(msalConfig);
      setMsalInstance(instance);
    };

    initializeMsal();
  }, []);

  const login = async () => {
    if (!msalInstance) {
      setError("MSAL instance not initialized yet.");
      return;
    }

    try {
      // Trigger MSAL login with correct scopes
      const loginResponse = await msalInstance.loginPopup({
        scopes: ["openid", "profile", "offline_access", "XboxLive.signin"],
      });

      console.log("Login response:", loginResponse);

      // Acquire Azure AD token
      const tokenResponse = await msalInstance.acquireTokenSilent({
        scopes: ["openid", "profile", "offline_access", "XboxLive.signin"],
        account: loginResponse.account,
      });

      console.log("Azure AD Token:", tokenResponse.accessToken);

      // Exchange Azure AD token for Xbox Live token
      const xboxLiveToken = await getXboxLiveToken(tokenResponse.accessToken);
      setXboxToken(xboxLiveToken);
      console.log("Xbox Live Token:", xboxLiveToken);
    } catch (err) {
      setError(err.message);
      console.error("Error during login:", err);
    }
  };

  const getXboxLiveToken = async (azureAdToken) => {
    const url = "https://user.auth.xboxlive.com/user/authenticate";
    const body = {
      Properties: {
        AuthMethod: "RPS",
        SiteName: "user.auth.xboxlive.com",
        RpsTicket: `d=${azureAdToken}`, // Add 'd=' prefix
      },
      RelyingParty: "http://auth.xboxlive.com",
      TokenType: "JWT",
    };

    try {
      const response = await fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(body),
      });

      if (!response.ok) {
        throw new Error(`Xbox Live authentication failed with status ${response.status}`);
      }

      const data = await response.json();
      return data.Token; // This is the Xbox Live token
    } catch (err) {
      setError(err.message);
      console.error("Error acquiring Xbox Live token:", err);
      return null;
    }
  };

  const loginAndSendToken = async () => {
    if (!xboxToken) {
      setError("No Xbox Live token available. Please log in first.");
      return;
    }
    try {
      // Send Xbox Live token to backend
      const tokenResponse = await fetch("http://localhost:8080/api/auth/xbox-token", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ xboxToken, createAccount: false }),
      });

      if (!tokenResponse.ok) {
        throw new Error(`Token server responded with ${tokenResponse.status}`);
      }

      const tokenData = await tokenResponse.text();
      console.log("Token sent to server:", tokenData);

      // Initiate Xbox login with PlayFab
      const xboxResponse = await fetch("http://localhost:8080/api/auth/xbox", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ xboxToken, createAccount: false }),
      });

      if (!xboxResponse.ok) {
        throw new Error(`Xbox server responded with ${xboxResponse.status}`);
      }

      const xboxData = await xboxResponse.json();
      console.log("Xbox server response:", xboxData);
    } catch (err) {
      setError(err.message);
      console.error("Error during login and token submission:", err);
    }
  };

  return (
    <div>
      <h1>Test Xbox Token Retrieval</h1>
      <button onClick={login} disabled={!msalInstance}>Login and Get Token</button>
      <button onClick={loginAndSendToken} disabled={!xboxToken}>Login Xbox Server</button>
      {xboxToken && <p>Xbox Live Token retrieved: {xboxToken}</p>}
      {error && <p style={{ color: "red" }}>Error: {error}</p>}
    </div>
  );
};

export default LoginWithMicrosoft;
