import { FaDesktop, FaXbox, FaPlaystation, FaGamepad } from "react-icons/fa";

export const checkAccountType = (platform) =>{
    if (!Array.isArray(platform)) {
        throw new Error("Expected an array of platforms");
    }
    let platfromCount = {
        pc :0,
        xbox:0,
        playstation:0,
        other:0
    };
    platform.forEach(platform => {
        switch (platform.toLowerCase()) {
        case "pc":
            platfromCount.pc++;
            break;
        case "xbox one":
        case "xbox series s/x":
        case "xbox":
            platfromCount.xbox++;
            break;
        case "playstation 5":
        case "ps4":
        case "ps5":
        case "playstation":
            platfromCount.playstation++;
            break;
        default:
            platfromCount.other++;
            break;
        }
    });
    // deteremine the platform
    if(platfromCount.xbox > 1){
        return <FaXbox style={{ marginRight: "5px" }} />;
    }else if(platfromCount.playstation > 1){
        return <FaPlaystation style={{ marginRight: "5px" }} />;
    }else if(platfromCount.pc > 1){
        return <FaDesktop style={{ marginRight: "5px" }} />;
    }else{
        return <FaGamepad style={{ marginRight: "5px" }} />;
    }
}
