            },
        });
        if(!response.ok){
            throw new Error(`Failed to fetch all news. Status: ${response.status}`);
        }
        const data = await response.json();
        const flattened = data.flatMap(item => item.results || []);
        return flattened;
    }catch(error){
        console.error(error);
        throw error;
    }
};
