// SwitchUserAccount.js
import { handleLogin } from "../service/XboxAuthService";
import { handleSteamLogin } from "../service/SteamAuthService";

export const switchUserAccount = (platform, navigate) => {
    localStorage.setItem("platform", platform);
    if (platform === "xbox") {
        if (!localStorage.getItem("uhs") || !localStorage.getItem("XSTS_token")) {
            handleLogin();
        } else {
            navigate("/main");
        }
    } else if (platform === "steam") {
        if (!localStorage.getItem("steamId")) {
            handleSteamLogin();
        } else {
            navigate("/main");
        }
    } else {
        // ...handle other platforms...
        navigate("/main");
    }
};
