import React, { useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const apiUrl = 'http://localhost:8080';

function OAuthCallback() {
    const { platform } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        const exchangeCodeForTokens = async () => {
            const urlParams = new URLSearchParams(window.location.search);
            const code = urlParams.get("code");

            if (code && platform) {
                try {
                    const response = await fetch(`${apiUrl}/api/auth/callback/${platform}?code=${code}`, {
                        method: "GET",
                        credentials: "include",
                    });

                    if (!response.ok) {
                        throw new Error(`Failed to retrieve tokens for ${platform}. Status: ${response.status}`);
                    }

                    const data = await response.json();

                    if (platform === "xbox") {
                        localStorage.setItem("uhs", data.uhs);
                        localStorage.setItem("XSTS_token", data.XSTS_token);
                    } else if (data.token) {
                        localStorage.setItem(`${platform}Token`, data.token);
                    } else {
                        throw new Error("Unexpected response format.");
                    }

                    toast.success(`${platform.toUpperCase()} login successful!`);
                    navigate("/main");
                } catch (error) {
                    console.error(error);
                    toast.error(`Error during ${platform} login: ${error.message}`);
                }
            }
        };

        exchangeCodeForTokens();
    }, [platform, navigate]);

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h1>Logging in with {platform ? platform.toUpperCase() : "Platform"}...</h1>
        </div>
    );
}

export default OAuthCallback;
