const apiUrl = 'http://localhost:8080';
const jwtToken = localStorage.getItem("jwtToken");

export const getSteamRecentPlayedAndOwnedGames = async () => {
    try{
        const response = await fetch(`${apiUrl}/api/steam/userinfo/getUserGameInfo`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                'Authorization': 'Bearer ' + jwtToken,
            },
        });
        if(!response.ok){
            throw new Error(`Failed to fetch Steam recent played and owned games. Status: ${response.status}`);
        }
        const data = await response.json();
        return data;
    }catch(error){
        console.error(error);
        console.error("Failed to fetch Steam recent played and owned games.");
        throw error;
    }
};
