import React, { useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import { toast } from "react-toastify";

const apiUrl = 'http://localhost:8080';

function LandingPage() {
    const navigate = useNavigate();

    const validateLocalSession = () => {
        const platform = localStorage.getItem("platform");

        console.log("Validating session for platform:", platform);

        if (platform) {
            // Check for platform-specific tokens
            if (platform === "xbox") {
                const uhs = localStorage.getItem("uhs");
                const XSTS_token = localStorage.getItem("XSTS_token");
                console.log("Xbox tokens - UHS:", uhs, "XSTS_token:", XSTS_token);
                if (uhs && XSTS_token) {
                    return true; // Xbox tokens are valid
                }
            } else {
                const token = localStorage.getItem(`${platform}Token`);
                console.log(`${platform} token:`, token);
                if (token) {
                    return true; // Generic token for PSN/Steam is valid
                }
            }
        }

        // If tokens are missing or invalid
        console.log("Invalid or missing tokens. Clearing platform.");
        localStorage.removeItem("platform");
        return false;
    };

    useEffect(() => {
        if (validateLocalSession()) {
            navigate("/main"); // Navigate to the main page if session is valid
        }
    }, [navigate]);


    return (
        <div className="landing-page" style={{ textAlign: "center", marginTop: "50px" }}>
            <h1>Welcome to GamerHub</h1>
            <p>Select a platform to log in:</p>
            <div style={{ display: "flex", justifyContent: "center", gap: "20px", marginTop: "20px" }}>
                <button onClick={() => handleLogin("xbox")} style={buttonStyle("green")}>
                    Xbox Login
                </button>
                <button onClick={() => handleLogin("psn")} style={buttonStyle("blue")}>
                    PSN Login
                </button>
                <button onClick={() => handleLogin("steam")} style={buttonStyle("gray")}>
                    Steam Login
                </button>
            </div>
        </div>
    );
}

const buttonStyle = (color) => ({
    padding: "10px 20px",
    fontSize: "16px",
    cursor: "pointer",
    backgroundColor: color,
    color: "#ffffff",
    border: "none",
    borderRadius: "5px",
});

export default LandingPage;
