// src/pages/NewsDetails.jsx
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getGameDetailById } from "../service/NewsService"; // Adjust this function if needed
import "../assests/News.css";

function NewsDetails() {
  const { gameId } = useParams();
  const [game, setGame] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function loadGame() {
      try {
        setLoading(true);
        // Adjust getGameDetailById so that it accepts the gameId parameter
        const data = await getGameDetailById(gameId);
        setGame(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    }
    loadGame();
  }, [gameId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;
  if (!game) return <p>Game not found.</p>;

  return (
    <div className="game-details-page">
      <h2>{game.name}</h2>
      <p>Released: {game.released}</p>
      <p>Average Rating: {game.averageRating}</p>
      {/* Display more game details as needed */}
      <img
        src={game.background_image}
        alt={game.name}
        style={{ width: "300px", height: "auto" }}
      />

      {game.short_screenshots && (
        <div style={{ display: "flex", gap: "0.5rem", overflowX: "auto" }}>
          {game.short_screenshots.map((shot, index) => (
            <img
              key={index}
              src={shot.image}
              alt={`${game.name} screenshot ${index + 1}`}
              style={{ width: "150px", height: "auto" }}
            />
          ))}
        </div>
      )}
    </div>
  );
}

export default NewsDetails;
