import React from "react";
import { useNavigate } from "react-router-dom";

function LandingPage() {
    const navigate = useNavigate();

    const handleNavigation = (platform) => {
        navigate(`/${platform}-login`);
    };

    return (
        <div className="landing-page" style={{ textAlign: "center", marginTop: "50px" }}>
            <h1>Welcome to GamerHub</h1>
            <p>Select a platform to log in:</p>
            <div style={{ display: "flex", justifyContent: "center", gap: "20px", marginTop: "20px" }}>
                <button
                    onClick={() => handleNavigation("xbox")}
                    style={buttonStyle("green")}
                >
                    Xbox Login
                </button>
                <button
                    onClick={() => handleNavigation("psn")}
                    style={buttonStyle("blue")}
                >
                    PSN Login
                </button>
                <button
                    onClick={() => handleNavigation("steam")}
                    style={buttonStyle("gray")}
                >
                    Steam Login
                </button>
            </div>
        </div>
    );
}

const buttonStyle = (color) => ({
    padding: "10px 20px",
    fontSize: "16px",
    cursor: "pointer",
    backgroundColor: color,
    color: "#ffffff",
    border: "none",
    borderRadius: "5px",
});

export default LandingPage;
